            children[c] = node;
        }
    };

    Node* root;
    Node* activeNode;
    int activeEdge;
    int activeLength;
    int remaining;

    string text;

public:
    SuffixTree(string text) {
        this->text = text;
        root = new Node(-1, -1);
        activeNode = root;
        activeEdge = -1;
        activeLength = 0;
        remaining = 0;
        build();
    }

    void print() {
        printHelper(root, 0);
    }

private:
    Node* split(Node* node) {
        if (activeLength == 0) {
            return node;
        }

        char startChar = text[node->start + activeEdge];
        Node* child = node->children[startChar];
        int splitEnd = node->start + activeEdge + activeLength - 1;

        if (child->getLength() == activeLength) {
            return child;
        }

        Node* splitNode = new Node(child->start, child->start + activeLength);
        node->addChild(splitNode);
        child->start += activeLength;
        splitNode->addChild(child);
        return splitNode;
    }

    void extend(int index) {
        remaining++;
        Node* lastNode = nullptr;

        while (remaining > 0) {
            if (activeLength == 0) {
                activeEdge = index;
            }

            char nextChar = text[index];
            if (activeNode->children[nextChar] == nullptr) {
                Node* newNode = new Node(index, text.size());
                activeNode->addChild(newNode);
                if (lastNode != nullptr) {
                    lastNode->suffixLink = activeNode;
                }
                lastNode = activeNode;
            } else {
                Node* nextNode = activeNode->children[nextChar];
                if (walkDown(nextNode)) {
                    continue;
                }

                char nextChar2 = text[nextNode->start + activeLength];
                if (nextChar == nextChar2) {
                    activeLength++;
