                        lastNode->suffixLink = activeNode;
                    }
                    break;
                }

                Node* splitNode = split(nextNode);
                Node* newNode = new Node(index, text.size());
                activeNode->addChild(splitNode);
                newNode->addChild(splitNode);
                activeNode->addChild(newNode);
                if (lastNode != nullptr) {
                    lastNode->suffixLink = newNode;
                }
                lastNode = newNode;
            }

            remaining--;
            if (activeNode == root && activeLength > 0) {
                activeLength--;
                activeEdge = index - remaining + 1;
            } else {
                activeNode = activeNode->suffixLink != nullptr ? activeNode->suffixLink : root;
            }
        }
    }

    bool walkDown(Node* node) {
        int length = node->getLength();
        if (activeLength >= length) {
            activeEdge += length;
            activeLength -= length;
            activeNode = node;
            return true;
        }
        return false;
    }

    void build() {
        for (int i = 0; i < text.size(); i++) {
            extend(i);
        }
    }

